   const sectionArray = localStorage.getItem('details');
   if (sectionArray) {
      setLsSection(JSON.parse(sectionArray).section)
      if (lsSection === '') {
         setShowSection(false)
      } else {
         setShowSection(true)
      }

      } else {
   setLsSection([])   
   } 

   }, [])

