      <>
      <strong class="text-uppercase mr-3" key={index}>{index+1}. {item.name}</strong>
      <p><strong>Content:</strong> {item.content.length > 15 ? `${item.content.substring(0, 15)}...` :item.content == '' ? 'No Content': <>{item.content}</>}</p>
