import React, { useState } from 'react';
import { Button } from '../ButtonElement';

import {
 SidebarContainer,
 Icon,
 CloseIcon,
 SidebarWrapper,
 SidebarMenu,
 SidebarLink,
} from './SidebarElements';
const Sidebar = ({ isOpen, toggle }) => {
 const [hover, setHover] = useState(false);

 const onHover = () => {
  setHover(!hover)
 }
 return (
  <SidebarContainer isOpen={isOpen} onClick={toggle}>
   <Icon onClick={toggle}>
    <CloseIcon/>
   </Icon>
   <SidebarWrapper>
    <SidebarMenu>
     <SidebarLink to="about" onClick={toggle}>About</SidebarLink>
     <SidebarLink to="courses" onClick={toggle}>Courses </SidebarLink>
     <SidebarLink to="tutors" onClick={toggle}>Tutors </SidebarLink>
     <SidebarLink to="footer" onClick={toggle}>Contact </SidebarLink>
     <SidebarLink to="signup" onClick={toggle}>
     <Button to="signup" onMouseEnter={onHover} onMouseLeave={onHover}>
      <>Create a Course <i class="fal fa-arrow-right"></i></> 
     </Button>
     </SidebarLink>



    </SidebarMenu>

   </SidebarWrapper>
  </SidebarContainer>
 )
}

export default Sidebar;
