import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import Swal from 'sweetalert2'

const Details = () => {

  const [show, setShow] = useState(false)
  useEffect(() => {


    if (localStorage.details) {
     setShow(true)
    } else {
      setShow(false)
    }
  })

  const [lsDetails, setLsDetails] = useState("")
  const [details, setDetails] = useState({category: '', title: '', cost: '', desc: '', section: [] });
  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value
    setDetails({...details, [name]:value})
  }

 const handleSubmit = (e) => {
   e.preventDefault();
   localStorage.setItem('details', JSON.stringify(details))
   let allDetails = JSON.parse(localStorage.getItem('details') || {});
   setLsDetails(allDetails)

}

 return (
  <div>
  <form data-aos="fade-up" onSubmit={handleSubmit}> 
   <div class="form-group">
   <p style={{fontFamily: '"Poppins" sans-serif', color: '#777777' }} className="font-weight-bold"><i class="fas fa-info-circle"></i> Course Basic Details <br /> <span>
   <small style={{ fontFamily: '"Poppins" sans-serif', color: '#777777' }}>You can always edit your details even after saved</small><br/>
   </span></p>

    {/* {show ?  <div className="row">
