   <small style={{ fontFamily: '"Poppins" sans-serif', color: '#777777' }}><i class="fas fa-info-circle"></i> You can click the Post Course button if there is not files to attach</small>
   <button className="btn  g px-3 shadow font-weight-bold" style={{
    backgroundColor: '#5FCF80',
    color:'white'
   }} to="/dashboard/createcourse/files">Next <i class="fal fa-long-arrow-right"></i></button>

  </div>
 )
}

export default Files;
