import React, {useState, useEffect} from 'react'
import {Link, useHistory} from 'react-router-dom'
import Swal from 'sweetalert2'

const Toast2 = Swal.mixin({
  toast: true,
  position: 'top-end',
  showConfirmButton: false,
  timer: 1000,
  didOpen: (toast) => {
  toast.addEventListener('mouseenter', Swal.stopTimer)
  toast.addEventListener('mouseleave', Swal.resumeTimer)
  }
})


const Details = () => {
let history = useHistory();
 const [show, setShow] = useState(false)
 const [lsDetails, setLsDetails] = useState("")
 const [details, setDetails] = useState({category: '', title: '', cost: '', desc: '', section: [] });
 const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value
    setDetails({...details, [name]:value})
  }

 const handleSubmit = (e) => {
   e.preventDefault();
   if (details.category && details.title && details.desc) {
    localStorage.setItem('details', JSON.stringify(details))
   let allDetails = JSON.parse(localStorage.getItem('details') || {});
   setLsDetails(allDetails)
   setShow(true) 
   } else {
    Toast2.fire({
      icon: 'error',
      title: 'Please fill details'
    })
   }

 }

  const handleClick = (e) => {
  e.preventDefault();
  Swal.fire({
    position: 'top-end',
    icon: 'success',
    title: 'Course Details saved',
    showConfirmButton: false,
    timer: 1000,
  })

}

 return (
  <div>
  <form data-aos="fade-up" onSubmit={handleSubmit}> 
   <div class="form-group">
   <p style={{fontFamily: '"Poppins" sans-serif', color: '#777777' }} className="font-weight-bold"><i class="fas fa-info-circle"></i> Course Basic Details <br /> <span>
   <small style={{ fontFamily: '"Poppins" sans-serif', color: '#777777' }}>You can always change details even after saving</small><br/>
   </span></p>

    {show ? <div className="row">
     <div className="col-md-3">
     <div class="alert alert-success alert-dismissible fade show tx" role="alert">
      Category: <strong>{lsDetails.category}</strong>
      </div>
      </div>

      <div className="col-md-3">
      <div class="alert alert-success alert-dismissible fade show tx" role="alert">
      Title: <strong>{lsDetails.title}</strong>
      </div>
      </div>

      <div className="col-md-3">
      <div class="alert alert-success alert-dismissible fade show tx" role="alert">
      Cost: {lsDetails.cost == "" ? <strong>Free</strong> : <strong>{lsDetails.cost}</strong>}
      </div>
      </div>

      <div className="col-md-3">
      <div class="alert alert-success alert-dismissible fade show tx justify-content-center" role="alert">
      Description: <strong>{lsDetails.desc}</strong>
      </div>
      </div>
      </div> : null}




     <label for="exampleFormControlSelect1" className="tx" style={{fontFamily: '"Poppins" sans-serif', color:'#5fcf80'}}>Category</label>
    <select value={details.category} class="form-control" id="exampleFormControlSelect1"   onChange={handleChange} name="category">
      <option>React</option>
      <option>Angular</option>
      <option>Vue</option>
      <option>HTML</option>
      <option>Bootstrap</option>
      <option>Graphics Design</option>
      <option>Andriod Development</option>
      <option>GIT</option>
      <option>Vue</option>
      <option>Laravel</option>
      <option>PHP</option>
      <option>Java</option>
      <option>Others</option>
    </select>
  </div>
  <div class="form-group">
    <label for="exampleFormControlInput1" className="tx" style={{fontFamily: '"Poppins" sans-serif', color:'#5fcf80'}}>Course Title</label>
    <input value={details.title} type="text" class="form-control" id="exampleFormControlInput1" placeholder="Type course title" name="title" onChange={handleChange}/>
    </div>
    <div class="form-group">
  <label for="exampleFormControlSelect1" className="tx" style={{fontFamily: '"Poppins" sans-serif', color:'#5fcf80'}}>Cost</label>
  <input value={details.cost} type="number" class="form-control" id="exampleFormControlInput1" placeholder="Type course amount" name="cost"   onChange={handleChange}/>
  <label class="tx font-weight-bold">
    Leave blank if course is Free
  </label>
  </div>
  <div class="form-group">
    <label for="exampleFormControlTextarea1" className="tx" style={{fontFamily: '"Poppins" sans-serif', color:'#5fcf80'}}>Description</label>
    <textarea value={details.desc} class="form-control" id="exampleFormControlTextarea1" rows="3" name="desc"  onChange={handleChange}/>
  </div>
  <button className="btn  g px-3 shadow font-weight-bold" style={{
    backgroundColor: '#5FCF80',
    color: 'white',
    fontFamily:'"Ubuntu", sans-serif'
    }}>Preview</button>

    {details.category == '' || details.title == ''|| details.desc == '' ? null :  <button className="btn  g px-3 shadow font-weight-bold ml-4" style={{
    backgroundColor: '#5FCF80',
    color: 'white',
    fontFamily:'"Ubuntu", sans-serif'
  }} onClick={handleClick}>Save</button>}   


   </form>
     </div>
 )
}

export default Details;
